function TanStackIcon({ className }: IconProps) {
  return <Image src={tanstack} alt="TanStack" className={clsx("max-w-fit", className)} />;
}

const icons = {
  next: NextIcon,
  remix: RemixIcon,
  vite: ViteIcon,
  svelte: SvelteIcon,
  solid: SolidIcon,
  qwik: QwikIcon,
  tanstack: TanStackIcon,
  cli: TerminalSquare,
  unknown: CircleSlash,
};

type BrandIconKey = keyof typeof icons;
type BrandIcon = (typeof icons)[BrandIconKey];

function isBrandIcon(icon: string): icon is BrandIconKey {
  return icon in icons;
}

type Props = {
  icon: BrandIconKey | (string & {});
  className?: string;
};

export function BrandIcon({ icon, className }: Props) {
  let Icon: BrandIcon = icons.unknown;
  if (isBrandIcon(icon)) {
    Icon = icons[icon];
  }
  return <Icon className={className} />;
}
